BubbleSort.code:
void bubbleSort(int arr[], int n){
	int i, j;
	for (i = 0; i < n - 1; i++)
		for (j = 0; j < n - i - 1; j++)
			if (arr[j] > arr[j + 1]) 
				swap(&arr[j], &arr[j + 1]);
}

InsertionSort.code:
void insertionSort(int arr[], int n){
	int i, key, j;
	for (i = 1; i < n; i++){
		key = arr[i];
		j = i - 1;
		while (j >= 0 && arr[j] > key){
			arr[j + 1] = arr[j];
			j = j - 1;
		}
		arr[j + 1] = key;
	}
}

SelectionSort.code:
void selectionSort(int arr[], int n){
	int i, j, min_idx;
	for (i = 0; i < n - 1; i++){
		min_idx = i;
		for (j = i + 1; j < n; j++)
			if (arr[j] < arr[min_idx]) min_idx = j;
		swap(&arr[min_idx], &arr[i]);
	}
}

MergeSort.code:
void merge(int arr[], int l, int m, int r){
	int i, j, k;
	int n1 = m - l + 1;
	int n2 = r - m;
	int L[n1], R[n2];
	for (i = 0; i < n1; i++) L[i] = arr[l + i];
	for (j = 0; j < n2; j++) R[j] = arr[m + 1 + j];
	i = 0;
	j = 0;
	k = l;
	while (i < n1 && j < n2){
		if (L[i] <= R[j]){
			arr[k] = L[i];
			i++;
		}
		else{
			arr[k] = R[j];
			j++;
		}
		k++;
	}
	while (i < n1){
		arr[k] = L[i];
		i++;
		k++;
	}
	while (j < n2){
		arr[k] = R[j];
		j++;
		k++;
	}
}
void mergeSort(int arr[], int l, int r){
	if (l < r){
		int m = l + (r - l) / 2;
		mergeSort(arr, l, m);
		mergeSort(arr, m + 1, r);
		merge(arr, l, m, r);
	}
}

LinearSearch.code:
int linearSearch(int arr[], int n, int x){
	int i;
	for (i = 0; i < n; i++)
		if (arr[i] == x)
			return i;
	return -1;
}

BinarySearch.code:
int binarySearch(int arr[], int l, int r, int x){
	if (r >= l){
		int mid = l + (r - l) / 2;
		if (arr[mid] == x)
			return mid;
		if (arr[mid] > x)
			return binarySearch(arr, l, mid - 1, x);
		return binarySearch(arr, mid + 1, r, x);
	}
	return -1;
}

JumpSearch.code:
int jumpSearch(int arr[], int x, int n){
	int step = sqrt(n);
	int prev = 0;
	while (arr[min(step, n) - 1] < x){
		prev = step;
		step += sqrt(n);
		if (prev >= n)
			return -1;
	}
	while (arr[prev] < x){
		prev++;
		if (prev == min(step, n))
			return -1;
	}
	if (arr[prev] == x)
		return prev;
	return -1;
}

InterpolationSearch.code:
int interpolationSearch(int arr[], int n, int x){
	int lo = 0, hi = (n - 1);
	while (lo <= hi && x >= arr[lo] && x <= arr[hi]){
		if (lo == hi){
			if (arr[lo] == x) return lo;
			return -1;
		}
		int pos = lo + (((double)(hi - lo) / 
		(arr[hi] - arr[lo])) * (x - arr[lo]));

		if (arr[pos] == x)
			return pos;
		if (arr[pos] < x)
			lo = pos + 1;
		else
			hi = pos - 1;
	}
	return -1;
}
